mapper.sql:
select aii.issue_id from ( SELECT ai.issue_id, ai.summary, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS issue_num FROM agile_issue ai LEFT JOIN agile_project_info api ON ai.project_id = api.project_id left join agile_issue aii2 on aii2.project_id in
#{projectId}
and ai.parent_issue_id = aii2.issue_id WHERE ai.project_id in
#{projectId}
AND ai.apply_type = 'agile'
) aii
AND aii.issue_num LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND (aii.issue_num LIKE CONCAT(CONCAT('%', #{content}), '%') OR aii.summary LIKE CONCAT(CONCAT('%', #{content}), '%'))

SELECT ai.issue_id FROM agile_issue ai LEFT JOIN agile_project_info api ON ai.project_id = api.project_id left join agile_issue aii2 on aii2.project_id in
#{projectId}
and ai.parent_issue_id = aii2.issue_id WHERE ai.project_id in
#{projectId}
AND ai.apply_type = 'agile'

mapper.select:
SELECT abc.column_id AS abc_column_id, abc.category_code AS abc_category_code, abc.board_id AS abc_board_id, abc.min_num AS abc_min_num, abc.max_num AS abc_max_num, abc.name AS abc_name, abc.sequence AS abc_sequence, alv1.name AS abc_color, abc.object_version_number AS abc_object_version_number, ais.id AS ais_id, ais.is_completed AS ais_completed, ais.status_id as ais_status_id, ais.object_version_number AS ais_object_version_number, ai.issue_id AS ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS ai_issue_num, ai.summary AS ai_summary, ai.assignee_id AS ai_assignee_id, ai.priority_id AS ai_priority_id, ai.issue_type_id AS ai_issue_type_id, ai.parent_issue_id AS ai_parent_issue_id, ai.type_code AS ai_type_code, ai.rank AS ai_rank, ai.epic_id AS ai_epic_id, ai.stay_date AS ai_stay_date, ai.estimated_start_time as ai_estimated_start_time, ai.estimated_end_time as ai_estimated_end_time, ai.object_version_number AS ai_object_version_number, ai.relate_issue_id AS ai_relate_issue_id, IF(fsb.instance_id IS NULL, NULL, TRUE) AS ai_starBeacon, abcsr.position as ais_position FROM agile_board_column abc LEFT JOIN agile_board_column_status_rel abcsr ON abc.column_id = abcsr.column_id LEFT JOIN agile_issue_status ais ON ais.project_id = abc.project_id and ais.status_id = abcsr.status_id LEFT JOIN (SELECT ai1.* from agile_issue ai1, agile_issue_sprint_rel aisr WHERE aisr.sprint_id = #{sprintId} AND ai1.issue_id = aisr.issue_id AND ai1.type_code in ('story', 'task', 'bug', 'sub_task')
AND ai1.issue_id IN (${filterSql})
AND ai1.issue_id IN ()
) ai ON ai.status_id = ais.status_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN lookup_value alv1 ON abc.color_code = alv1.value_code LEFT JOIN fd_star_beacon fsb ON fsb.project_id in
#{projectId}
AND fsb.type = 'issue' AND fsb.user_id = #{userId} AND ai.issue_id = fsb.instance_id WHERE abc.project_id in
#{projectId}
AND abc.board_id = #{boardId} ORDER BY abc.sequence

SELECT abc.column_id AS abc_column_id, abc.category_code AS abc_category_code, abc.board_id AS abc_board_id, abc.min_num AS abc_min_num, abc.max_num AS abc_max_num, abc.name AS abc_name, abc.sequence AS abc_sequence, alv1.name AS abc_color, abc.object_version_number AS abc_object_version_number, ais.id AS ais_id, ais.name AS ais_name, ais.category_code AS ais_category_code, ais.is_completed AS ais_completed, ais.object_version_number AS ais_object_version_number, ai.issue_id AS ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS ai_issue_num, ai.summary AS ai_summary, ai.assignee_id AS ai_assignee_id, alv.name AS ai_priority_name, ai.parent_issue_id AS ai_parent_issue_id, ai.type_code AS ai_type_code, ai.object_version_number AS ai_object_version_number FROM agile_board_column abc LEFT JOIN agile_board_column_status_rel abcsr ON abc.column_id = abcsr.column_id LEFT JOIN agile_issue_status ais ON abcsr.status_id = ais.id LEFT JOIN (SELECT ai1.* from agile_issue ai1 WHERE ai1.issue_id IN
#{issueId}
) ai ON ai.status_id = ais.id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN lookup_value alv ON ai.priority_code = alv.value_code LEFT JOIN lookup_value alv1 ON abc.color_code = alv1.value_code WHERE abc.board_id = #{boardId} ORDER BY abc.sequence

mapper.update:
UPDATE agile_board_column
SET sequence              = sequence + 1,
    object_version_number = object_version_number + 1
WHERE board_id = #{boardId}
      AND sequence >= #{sequence} AND sequence < #{originSequence}

UPDATE agile_board_column
SET sequence              = sequence - 1,
    object_version_number = object_version_number + 1
WHERE board_id = #{boardId}
      AND sequence > #{originSequence} AND sequence <= #{sequence}

UPDATE agile_board_column
SET max_num               = #{columnInfo.maxNum},
    min_num               = #{columnInfo.minNum},
    object_version_number = object_version_number + 1
WHERE
    project_id = #{columnInfo.projectId}
    AND column_id = #{columnInfo.columnId}
    AND object_version_number = #{columnInfo.objectVersionNumber}

mapper.select:
SELECT abc.column_id ,abc.name,alv.name as color,abc.category_code from agile_board_column abc left join lookup_value alv on alv.type_code = 'column_color' and alv.value_code = abc.color_code where abc.column_id in
#{columnId}
ORDER BY abc.sequence

delete.foreach:
DELETE ais,abcsr from agile_issue_status ais left join agile_board_column_status_rel abcsr on ais.status_id = abcsr.status_id and ais.project_id = abcsr.project_id WHERE ais.status_id in
#{deleteStatusId}
and ais.project_id = #{removeStatusWithProject.projectId}

mapper.delete:
DELETE FROM agile_board_column_status_rel
WHERE
    status_id = #{statusId}
  AND project_id = #{projectId}

mapper.select:
select ai1.issue_id from ( select ai.issue_id, CONVERT(ai.issue_num,UNSIGNED) as issue_num_convert from agile_issue ai, agile_issue_status ais where ai.project_id = #{projectId} and ai.status_id = ais.status_id and ais.project_id = #{projectId} and ais.is_completed = 1 and ( select count(ai2.issue_id) from agile_issue ai2, agile_issue_status ais2 where ai2.project_id = #{projectId} and ai2.parent_issue_id = ai.issue_id and ai2.status_id = ais2.status_id and ais2.project_id = #{projectId} and ais2.is_completed = 0 ) = 0 and ai.issue_id in
#{parentId}
) ai1 order by ai1.issue_num_convert

select * from agile_board_column_status_rel abcsr
where abcsr.column_id IN

    #{columnId}

SELECT
 abcsr.status_id
FROM
 agile_board_column_status_rel abcsr,agile_issue_status ais
where  abcsr.project_id = #{projectId}
and abcsr.column_id in (
  select abc.column_id from agile_board_column abc
  where abc.project_id = #{projectId}
  AND abc.board_id = #{boardId}
)
AND ais.project_id = abcsr.project_id
AND ais.status_id = abcsr.status_id

SELECT ai.status_id, ai.issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS issue_num, ai.summary, ai.assignee_id, ai.priority_id, ai.issue_type_id, ai.parent_issue_id, ai.type_code, ai.rank, ai.epic_id, ai.stay_date, ai.estimated_start_time, ai.estimated_end_time, ai.object_version_number, ai.relate_issue_id, IF(fsb.instance_id IS NULL, NULL, TRUE) AS starBeacon FROM agile_issue ai LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_issue_sprint_rel aisr ON ai.issue_id = aisr.issue_id AND aisr.project_id in
#{projectId}
LEFT JOIN fd_star_beacon fsb ON fsb.project_id in
#{projectId}
AND fsb.type = 'issue' AND fsb.user_id = #{userId} AND ai.issue_id = fsb.instance_id WHERE ai.project_id in
#{projectId}
AND aisr.sprint_id = #{sprintId} AND ai.type_code in ('story', 'task', 'bug', 'sub_task')
AND ai.status_id IN
#{statusId}
and ai.is_pre_sprint_done = 0
AND CONCAT_WS('-', api.project_code, ai.issue_num) LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND (CONCAT_WS('-', api.project_code, ai.issue_num) LIKE CONCAT(CONCAT('%', #{content}), '%') OR ai.summary LIKE CONCAT(CONCAT('%', #{content}), '%'))
