mapper.select:
SELECT ai.status_id, ai.issue_id, avcsr.column_id FROM agile_issue ai LEFT JOIN agile_board_column_status_rel avcsr ON ai.status_id = avcsr.status_id WHERE ai.issue_id in
#{issueId}
AND avcsr.column_id IN
#{columnId}

select * from agile_board_column_status_rel abcsr
where abcsr.organization_id = #{organizationId}
and abcsr.project_id = #{projectId}
and abcsr.column_id = #{columnId}
order by abcsr.position asc

mapper.update:
update agile_board_column_status_rel abcsr
set abcsr.position = #{columnStatusRel.position}
where abcsr.organization_id = #{columnStatusRel.organizationId}
and abcsr.project_id = #{columnStatusRel.projectId}
and abcsr.column_id = #{columnStatusRel.columnId}
and abcsr.status_id = #{columnStatusRel.statusId}

mapper.select:
select abcsr.status_id
from agile_board_column_status_rel abcsr
where abcsr.organization_id = #{organizationId}
and abcsr.project_id = #{projectId}
and abcsr.column_id in (
      select column_id from agile_board_column
      where board_id = #{boardTemplateId}
)
