mapper.delete:
delete from fd_field_permission
where id in

    #{id}

delete ffp.*
from fd_field_permission ffp
where ffp.organization_id = #{organizationId}

    and ffp.project_id = #{projectId}

and ffp.field_id = #{fieldId}

    and ffp.issue_type_id = #{issueTypeId}

mapper.select:
select * from fd_field_permission
where organization_id = #{organizationId}
and project_id = #{projectId}
and field_id in

    #{fieldId}


    and issue_type_id in

        #{issueTypeId}

select
ffp.field_id
from
fd_field_permission ffp
where ffp.organization_id = #{organizationId}
and ffp.project_id = #{projectId}
and ffp.permission = #{permissionVO.scope}
and ffp.issue_type_id in

    #{issueTypeId}

and ffp.field_id in

    #{fieldId}

and (
    (
        ffp.type = 'user'
        and ffp.role_member_id in

            #{userId}

    )

        or (
            ffp.type = 'role'
            and ffp.role_member_id in

                #{roleId}

        )

)
union
select fosf.id
from fd_object_scheme_field fosf
left join fd_field_permission ffp on (
    ffp.organization_id = #{organizationId}
    and ffp.project_id = #{projectId}
    and fosf.id = ffp.field_id
    and ffp.permission = #{permissionVO.scope}
    and ffp.issue_type_id in

        #{issueTypeId}

)
where fosf.id in

    #{fieldId}

and ffp.id is null

select
count(ffp.id) != 0 as configured
from fd_field_permission ffp
where ffp.organization_id = #{organizationId}
and ffp.project_id = #{projectId}

    and ffp.issue_type_id in

        #{issueTypeId}

select
fosf.id as field_id,
ffp.issue_type_id,
ffp.role_member_id,
ffp.type,
ffp.permission
from fd_object_scheme_field fosf
left join fd_field_permission ffp on (
    ffp.organization_id = #{organizationId}
    and ffp.project_id = #{projectId}
    and fosf.id = ffp.field_id

        and ffp.permission = #{permissionVO.scope}

    and ffp.issue_type_id in

        #{issueTypeId}

)
where fosf.id in

    #{fieldId}

select ffp.*
from fd_field_permission ffp
where 1=1

    AND ffp.project_id in

        #{projectId}



    and ffp.field_id in

        #{fieldId}



    and ffp.issue_type_id = #{issueTypeId}
