mapper.update:
update
agile_issue_comment set parent_id = 0
where project_id = #{projectId}
and parent_id = #{commentId}

update
agile_issue_comment set parent_id = #{newCommentId}
where project_id = #{projectId}
and parent_id = #{oldCommentId}

mapper.select:
SELECT comment_id, user_id, issue_id, comment_text, project_id, parent_id, reply_to_user_id, object_version_number, created_by, creation_date, last_updated_by, last_update_date FROM agile_issue_comment WHERE project_id = #{projectId} AND issue_id = #{issueId} AND parent_id IN
#{commentId}
ORDER BY comment_id DESC
