mapper.delete:
DELETE FROM agile_issue_label WHERE label_id IN ( SELECT d.id FROM ( SELECT ail.label_id AS id FROM agile_issue_label ail LEFT JOIN agile_label_issue_rel ali ON ali.label_id = ail.label_id WHERE ali.issue_id IS NULL and ail.project_id = #{projectId}
AND ail.label_id NOT IN
#{labelId}
) d )

mapper.select:
SELECT count(ail.label_id)
FROM agile_issue_label ail
WHERE project_id = #{projectId} AND label_name = #{labelName}

SELECT ail.label_id
FROM agile_issue_label ail
WHERE project_id = #{projectId} AND label_name = #{labelName}

select * from agile_issue_label
where project_id in

    #{projectId}
