mapper.select:
SELECT count(ail.label_id)
FROM agile_issue_label ail
WHERE project_id = #{projectId} AND label_name = #{labelName}

SELECT ail.label_id
FROM agile_issue_label ail
WHERE project_id = #{projectId} AND label_name = #{labelName}

select * from agile_issue_label
where project_id in

    #{projectId}

select alir.issue_id, alir.label_id, ail.label_name from agile_label_issue_rel alir left join agile_issue_label ail on ail.label_id = alir.label_id where alir.project_id in
#{projectId}
and alir.issue_id in
#{issueId}
