mapper.delete:
DELETE
FROM agile_issue_sprint_rel
WHERE project_id = #{projectId} AND sprint_id = #{sprintId}

mapper.select:
SELECT
avir.*,
apr.name
FROM agile_version_issue_rel avir LEFT JOIN agile_product_version apr ON avir.version_id = apr.version_id
WHERE avir.project_id in

    #{item}


    AND avir.relation_type = #{relationType}

AND avir.issue_id IN

    #{issueId}

SELECT aisr.issue_id, asp.sprint_name FROM agile_issue_sprint_rel aisr, agile_sprint asp WHERE aisr.project_id in
#{item}
AND aisr.sprint_id = asp.sprint_id AND asp.status_code = 'closed' AND aisr.issue_id IN
#{issueId}

SELECT ali.*, ail.label_name FROM agile_label_issue_rel ali LEFT JOIN agile_issue_label ail ON ali.label_id = ail.label_id WHERE ali.issue_id IN
#{issueId}
and ail.project_id in
#{item}

SELECT acir.*, aic.name FROM agile_component_issue_rel acir LEFT JOIN agile_issue_component aic ON aic.component_id = acir.component_id WHERE acir.issue_id IN
#{issueId}
and acir.project_id in
#{item}

SELECT *
FROM agile_issue_attachment
WHERE issue_id = #{issueId}

SELECT ai.issue_id, ai.type_code, ai.apply_type, ai.summary, ai.priority_code, ai.assignee_id, ai.reporter_id, ai.project_id as issue_project_id, ai.status_id, ai.priority_id, ai.issue_type_id, ai.created_by, ai.last_updated_by, ai3.epic_name, alv2.name as epic_color, ai.epic_id, ai.epic_name as epic_self_name, ai.creation_date, ai.last_update_date as issue_last_update_date, apv.version_id, apv.status_code as version_status_code, apv.name as version_name, avir.relation_type, avir.project_id as version_project_id, avir.issue_id as version_issue_id, CONCAT(api.project_code,'-', ai.issue_num) as issue_num, aisr.sprint_id, asp.status_code as sp_status_code, asp.sprint_name, aic.name as component_name, acir.component_id, ail.label_name, ail.label_id, ai.story_points, ai.remaining_time, ai.estimate_time,
ai.description, IF((ais.is_completed = 1), '是', '否') AS resolution,
ai.parent_issue_id, ai.relate_issue_id, ai.estimated_start_time, ai.estimated_end_time, ai.actual_start_time, ai.actual_end_time, ai.object_version_number, ais.is_completed as completed, ai.main_responsible_id, ai.environment, atir.tag_project_id, atir.app_service_code as app_service_code, atir.tag_name as tag_name, aipr.participant_id FROM agile_issue ai left join agile_issue_status ais on ais.status_id = ai.status_id and ais.project_id = ai.project_id left join agile_issue ai3 on ai.epic_id = ai3.issue_id LEFT JOIN lookup_value alv2 ON ai3.color_code = alv2.value_code LEFT JOIN agile_project_info api ON ai.project_id = api.project_id LEFT JOIN agile_version_issue_rel avir on avir.issue_id = ai.issue_id left join agile_product_version apv on avir.version_id = apv.version_id left join agile_issue_sprint_rel aisr on aisr.issue_id = ai.issue_id left join agile_sprint asp on aisr.sprint_id = asp.sprint_id left join agile_component_issue_rel acir on acir.issue_id = ai.issue_id left join agile_issue_component aic on aic.component_id = acir.component_id left join agile_label_issue_rel alir on alir.issue_id = ai.issue_id left join agile_issue_label ail on ail.label_id = alir.label_id left join agile_tag_issue_rel atir on ai.issue_id = atir.issue_id left join agile_issue_participant_rel aipr on ai.issue_id = aipr.issue_id WHERE ai.issue_id IN ( select aii.issue_id from agile_issue aii where aii.issue_id in
#{issueId}
or ( aii.parent_issue_id in
#{issueId}
and aii.issue_id in
#{id}
) or ( aii.relate_issue_id in
#{issueId}
and aii.issue_id in
#{id}
)
)
order by CASE ai.issue_id
when #{issueId} then #{index}
END, ai.issue_id

select ai.issue_id, ai.summary, ai.assignee_id, ai.estimated_start_time, ai.estimated_end_time, ai.estimate_time, ai.object_version_number, ai.issue_type_id, ai.parent_issue_id, ai.relate_issue_id, ai.status_id, ai.priority_id, CONVERT(ai.issue_num,SIGNED) as issue_num_convert, ai.issue_num, ai.actual_start_time, ai.actual_end_time,
agir.rank, agir.instance_id, agir.instance_type,
ai.epic_id, ai.epic_name, ai.project_id as issue_project_id, ai.type_code, ai.story_points, ai.main_responsible_id, ai.remaining_time, ai.environment, ai.apply_type, ai.created_by, ai.last_updated_by, ai.reporter_id, ai.creation_date, ai.last_update_date, aipr.participant_id, ai.actual_start_time, ai.actual_end_time from agile_issue ai
left join agile_gantt_issue_rank agir on ai.issue_id = agir.issue_id and agir.dimension = #{dimension}
left join agile_issue_participant_rel aipr on ai.project_id = aipr.project_id and ai.issue_id = aipr.issue_id
LEFT JOIN ( SELECT instance_id, date_value, option_id, string_value, text_value, number_value FROM fd_field_value ffv JOIN agile_issue ai ON ai.issue_id = ffv.instance_id AND ai.project_id in
#{projectId}
WHERE ffv.field_id = #{sortMap.sortFieldId} AND ffv.project_id in
#{projectId}
AND ai.issue_type_id IN
#{issueTypeId}
) fv ON ai.issue_id = fv.instance_id
where ai.project_id in
#{projectId}
and ai.issue_id in
#{issueId}
ORDER BY ${sortMap.orderStr}

select ai1.issue_id , ai1.priority_id, ai1.reporter_id, ai1.status_id, ai1.assignee_id, ai1.last_update_date, ai1.issue_num1 as issue_num, ai1.issue_type_id, ai1.estimated_start_time from ( select t1.issue_issue_id as issue_id, ai.priority_id, ai.reporter_id, ai.status_id, ai.assignee_id, ai.last_update_date, concat(api.project_code,'-', ai.issue_num) as issue_num1, CONVERT(ai.issue_num,SIGNED) as issue_num_convert, ai.issue_type_id, ai.created_by, ai.last_updated_by, ai.creation_date, ai.story_points, ai.estimated_start_time, ai.estimated_end_time, ai.main_responsible_id, ai.environment, ai.remaining_time from ( select
distinct case when search.type_code = 'sub_task' then search.parent_issue_id when search.type_code = 'bug' and search.relate_issue_id is not null and search.relate_issue_id != 0 then search.relate_issue_id else search.issue_issue_id end as issue_issue_id
distinct search.issue_issue_id
from (
)search where 1=1 and ( search.type_code != 'sub_task' or search.parent_type_code != 'bug' or search.parent_relate_issue_id is null or search.parent_relate_issue_id = 0 )
AND search.issue_num LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND (search.issue_num LIKE CONCAT(CONCAT('%', #{content}), '%') OR search.summary LIKE CONCAT(CONCAT('%', #{content}), '%'))
) t1 join agile_issue ai on t1.issue_issue_id = ai.issue_id left join agile_project_info api on ai.project_id = api.project_id
LEFT JOIN ( SELECT instance_id, date_value, option_id, string_value, text_value, number_value FROM fd_field_value ffv JOIN agile_issue ai ON ai.issue_id = ffv.instance_id AND ai.project_id = #{projectId} WHERE ffv.field_id = #{sortMap.sortFieldId} AND ffv.project_id = #{projectId} AND ai.issue_type_id IN
#{issueTypeId}
) fv ON ai.issue_id = fv.instance_id
ORDER BY ${sortMap.orderStr}
) ai1

SELECT ai.issue_id, ai.project_id,
agir.rank, agir.instance_id, agir.instance_type,
CONVERT(ai.issue_num,SIGNED) as issue_num_convert FROM agile_issue ai
left join agile_gantt_issue_rank agir on ai.issue_id = agir.issue_id and agir.dimension = #{searchVO.searchArgs.dimension}
LEFT JOIN ( SELECT instance_id, date_value, option_id, string_value, text_value, number_value FROM fd_field_value ffv JOIN agile_issue ai ON ai.issue_id = ffv.instance_id AND ai.project_id in
#{projectId}
WHERE ffv.field_id = #{sortMap.sortFieldId} AND ffv.project_id in
#{projectId}
AND ai.issue_type_id IN
#{issueTypeId}
) fv ON ai.issue_id = fv.instance_id
WHERE ai.project_id in
#{projectId}
AND ai.apply_type IN
#{applyType}
AND ai.apply_type = 'agile'
AND ai.issue_num LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND ( ai.issue_num LIKE CONCAT(CONCAT('%', #{content}), '%') OR ai.summary LIKE CONCAT(CONCAT('%', #{content}), '%') )
ORDER BY ${sortMap.orderStr}

select ai.issue_id, ai.project_id,
agir.rank, agir.instance_id, agir.instance_type,
CONVERT(ai.issue_num,SIGNED) as issue_num_convert from ( select temp.issue_id from ( SELECT case when ai.type_code = 'sub_task' then ai.parent_issue_id when ai.type_code = 'bug' and ai.relate_issue_id is not null and ai.relate_issue_id != 0 then ai.relate_issue_id else ai.issue_id end as issue_id FROM agile_issue ai WHERE ai.project_id in
#{projectId}
AND ai.apply_type = 'agile'
AND ai.issue_num LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND ( ai.issue_num LIKE CONCAT(CONCAT('%', #{content}), '%') OR ai.summary LIKE CONCAT(CONCAT('%', #{content}), '%') )
) temp group by temp.issue_id ) ai1 join agile_issue ai on ( ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ai1.issue_id = ai.issue_id )
left join agile_gantt_issue_rank agir on ai.issue_id = agir.issue_id and agir.dimension = #{searchVO.searchArgs.dimension}
LEFT JOIN ( SELECT instance_id, date_value, option_id, string_value, text_value, number_value FROM fd_field_value ffv JOIN agile_issue ai ON ai.issue_id = ffv.instance_id AND ai.project_id in
#{projectId}
WHERE ffv.field_id = #{sortMap.sortFieldId} AND ffv.project_id in
#{projectId}
AND ai.issue_type_id IN
#{issueTypeId}
) fv ON ai.issue_id = fv.instance_id
where ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ( ai.type_code in ('issue_epic', 'story', 'task') or (ai.type_code = 'bug' and (ai.relate_issue_id is null or ai.relate_issue_id = 0)) )
ORDER BY ${sortMap.orderStr}

select count(distinct temp.issue_id) from ( select case when ai.type_code = 'sub_task' then ai.parent_issue_id when ai.type_code = 'bug' and ai.relate_issue_id is not null and ai.relate_issue_id != 0 then ai.relate_issue_id else ai.issue_id end as issue_id from agile_issue ai where ai.project_id in
#{projectId}
and ai.apply_type = 'agile'
and ai.issue_num like CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
and ( ai.issue_num like CONCAT(CONCAT('%', #{content}), '%') or ai.summary like CONCAT(CONCAT('%', #{content}), '%') )
) temp join agile_issue ai on ai.issue_id = temp.issue_id where ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ( ai.type_code in ('issue_epic', 'story', 'task') or (ai.type_code = 'bug' and (ai.relate_issue_id is null or ai.relate_issue_id = 0)) )

select ai.issue_id, ai.project_id,
agir.rank, agir.instance_id, agir.instance_type,
CONVERT(ai.issue_num,SIGNED) as issue_num_convert from agile_issue ai
left join agile_gantt_issue_rank agir on ai.issue_id = agir.issue_id and agir.dimension = #{searchVO.searchArgs.dimension}
LEFT JOIN ( SELECT instance_id, date_value, option_id, string_value, text_value, number_value FROM fd_field_value ffv JOIN agile_issue ai ON ai.issue_id = ffv.instance_id AND ai.project_id in
#{projectId}
WHERE ffv.field_id = #{sortMap.sortFieldId} AND ffv.project_id in
#{projectId}
AND ai.issue_type_id IN
#{issueTypeId}
) fv ON ai.issue_id = fv.instance_id
where ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ai.type_code in ('sub_task', 'bug') and ( ai.parent_issue_id in
#{id}
or ai.type_code = 'bug' and ai.relate_issue_id in
#{id}
)
AND ai.issue_num LIKE CONCAT(CONCAT('%' ,#{searchVO.searchArgs.issueNum}) ,'%')
AND (ai.issue_num LIKE CONCAT(CONCAT('%', #{content}), '%') OR ai.summary LIKE CONCAT(CONCAT('%', #{content}), '%'))
ORDER BY ${sortMap.orderStr}

