ai.project_id = #{projectId}
AND
ai.apply_type = 'agile'
AND ai.issue_id in (
select ai2.issue_id from agile_issue ai2
where ai2.project_id = #{projectId}
AND ai2.type_code in ('sub_task','bug')
AND (
    ai2.parent_issue_id in

        #{issueId}

    or
    ai2.relate_issue_id in

        #{issueId}

)
)

