mapper.select:
select distinct assignee_id from agile_issue where project_id in
#{projectId}
and apply_type = 'agile' and assignee_id is not null

select * from ( select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai join agile_issue_status ais on ai.project_id = ais.project_id and ai.status_id = ais.status_id left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
and ai.apply_type = 'waterfall' and ai.assignee_id = #{userId} and ais.is_completed != 1 and ai.type_code in ('stage','milestone','activity')
union select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai join agile_issue_sprint_rel aisr on ai.issue_id = aisr.issue_id join agile_issue_status ais on ai.project_id = ais.project_id and ai.status_id = ais.status_id left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ai.assignee_id = #{userId} and aisr.sprint_id in
#{activeSprintId}
and ais.is_completed != 1 and ai.type_code in ('story','bug','task', 'sub_task')
) ai

select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai join agile_issue_sprint_rel aisr on ai.issue_id = aisr.issue_id join agile_issue_status ais on ai.project_id = ais.project_id and ai.status_id = ais.status_id left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and ai.assignee_id = #{userId} and aisr.sprint_id in
#{activeSprintId}
and ais.is_completed != 1 and ai.type_code = 'bug'

select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
AND ai.type_code in ('story','bug','task','sub_task','feature', 'stage','milestone','activity') AND ai.issue_id in ( select fsb.instance_id from fd_star_beacon fsb where fsb.type = 'issue' and fsb.project_id in
#{projectId}
and fsb.user_id = #{userId} )

select * from ( select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
and ai.apply_type = 'waterfall' and ai.reporter_id = #{userId} and ai.type_code in ('stage','milestone','activity')
union select ai.issue_id, ai.summary, ai.project_id, ai.type_code, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.reporter_id, ai.last_update_date, ai.priority_id, ai.assignee_id, ai.issue_type_id, ai.status_id, ai.parent_issue_id, ai.relate_issue_id, ai.creation_date, ai.estimated_start_time, ai.estimated_end_time from agile_issue ai join agile_issue_sprint_rel aisr on ai.issue_id = aisr.issue_id left join agile_project_info api ON ai.project_id = api.project_id where 1=1 and ai.project_id in
#{projectId}
and ai.apply_type = 'agile' and aisr.sprint_id in
#{activeSprintId}
