mapper.select:
select *
from agile_issue_sprint_rel aisr, agile_sprint asp
where aisr.project_id = #{projectId}
and aisr.issue_id = #{issueId}
and aisr.sprint_id = asp.sprint_id
and asp.status_code != 'closed'

select aisr.issue_id, asp.sprint_id, asp.sprint_name, asp.start_date, asp.end_date, asp.actual_end_date, asp.status_code from agile_issue_sprint_rel aisr join agile_sprint asp on aisr.sprint_id = asp.sprint_id where aisr.issue_id in
#{issueId}
and asp.status_code in
#{statusCode}

mapper.delete:
DELETE FROM agile_issue_sprint_rel
WHERE id IN

    #{issueSprintRelId}
