SELECT ais.id AS ais_id, ais.project_id AS ais_project_id, ais.status_id AS ais_status_id, ais.is_completed AS ais_completed, ais.object_version_number AS ais_object_version_number, ai.issue_id AS ai_issue_id, ai.summary AS ai_summary, ai.object_version_number AS ai_object_version_number FROM agile_issue_status ais LEFT JOIN agile_issue ai ON ai.status_id = ais.status_id and ai.project_id = #{projectId} WHERE ais.project_id = #{projectId} and ais.status_id in
#{id}
AND ( SELECT COUNT(1) AS num FROM agile_board_column_status_rel abcsr, agile_board_column abc WHERE abc.board_id = #{boardId} AND abcsr.column_id = abc.column_id AND ais.status_id = abcsr.status_id and ais.project_id = #{projectId} ) = 0
