mapper.select:
select t1.id, t1.organization_id, t1.object_version_number, t1.created_by, t1.creation_date, t1.last_updated_by, t1.last_update_date, t1.type_code, t1.project_id, t1.referenced, t1.source, t1.reference_id, t1.is_initialize as initialize, ifnull(t3.enabled, 1) as enabled, ifnull(t3.name, t1.name) as name, ifnull(t3.description, t1.description) as description, ifnull(t3.icon, t1.icon) as icon, ifnull(t3.colour, t1.colour) as colour, t2.sequence from fd_issue_type t1 left join fd_issue_type_scheme_config t2 on t2.issue_type_id = t1.id left join fd_issue_type_extend t3 on t1.id = t3.issue_type_id and t3.project_id = #{projectId} WHERE 1=1
and t1.organization_id = #{organizationId} and t1.project_id = #{projectId}
and t1.id in ( select t4.id from fd_issue_type t4 where t4.organization_id = #{organizationId} and t4.project_id = #{projectId} union select t5.id from fd_issue_type t5 where t5.organization_id = #{organizationId} and t5.project_id = 0 and t5.source = 'system' )
AND t2.scheme_id = #{schemeId}
and ifnull(t3.enabled, 1) = 1
order by t3.rank desc, t1.id desc

select * from fd_issue_type where organization_id in
#{organizationId}
and source = 'system' and project_id = 0

select t3.* from ( select t1.id, t1.organization_id, t1.object_version_number, t1.created_by, t1.creation_date, t1.last_updated_by, t1.last_update_date, t1.type_code, t1.project_id, t1.referenced, t1.source, t1.reference_id, t1.is_initialize as initialize, ifnull(t2.enabled, 1) as enabled, ifnull(t2.name, t1.name) as name, ifnull(t2.description, t1.description) as description, ifnull(t2.icon, t1.icon) as icon, ifnull(t2.colour, t1.colour) as colour, t2.rank as rank from fd_issue_type t1 left join fd_issue_type_extend t2 on t1.id = t2.issue_type_id and t2.project_id = #{projectId} where t1.organization_id = #{organizationId}
and t1.project_id = 0
and t1.id in ( select t2.id from fd_issue_type t2 where t2.organization_id = #{organizationId} and t2.project_id = #{projectId} union select t3.id from fd_issue_type t3 where t3.organization_id = #{organizationId} and t3.project_id = 0 and t3.source = 'system' )
and t1.type_code in
#{item}
and t1.id in
#{item}
) t3 where 1=1
and t3.name like concat('%',#{issueTypeSearchVO.name},'%')
and t3.description like concat('%',#{issueTypeSearchVO.description},'%')
and t3.source = #{issueTypeSearchVO.source}
and t3.enabled = #{issueTypeSearchVO.enabled}
and t3.referenced = #{issueTypeSearchVO.referenced}
and ( t3.name like concat('%',#{issueTypeSearchVO.param},'%') or t3.description like concat('%',#{issueTypeSearchVO.param},'%') or t3.source = #{issueTypeSearchVO.source} )
order by t3.rank desc, t3.id desc

select fit.*, fite.id as extend_id, fite.issue_type_id as extend_issue_type_id, fite.project_id as extend_project_id, fite.organization_id as extend_organization_id, fite.enabled as extend_enabled, fite.name as extend_name, fite.description as extend_description, fite.icon as extend_icon, fite.colour as extend_colour from fd_issue_type fit left join fd_issue_type_extend fite on ( fite.organization_id = #{organizationId} and fite.project_id in
#{projectId}
and fit.id = fite.issue_type_id ) where fit.id in
#{issueTypeId}

select t1.id, t1.organization_id, t1.object_version_number, t1.created_by, t1.creation_date, t1.last_updated_by, t1.last_update_date, t1.type_code, ifnull(t2.project_id, t1.project_id) as project_id, t1.referenced, t1.source, t1.reference_id, t1.is_initialize as initialize, ifnull(t2.enabled, 1) as enabled, ifnull(t2.name, t1.name) as name, ifnull(t2.description, t1.description) as description, ifnull(t2.icon, t1.icon) as icon, ifnull(t2.colour, t1.colour) as colour from fd_issue_type t1 left join fd_issue_type_extend t2 on t1.id = t2.issue_type_id where t1.organization_id = #{organizationId} and t1.id in ( select t2.id from fd_issue_type t2 where t2.organization_id = #{organizationId} and t2.project_id in
#{projectId}
union select t3.id from fd_issue_type t3 where t3.organization_id = #{organizationId} and t3.project_id = 0 and t3.source = 'system' ) UNION ( SELECT t1.id, t1.organization_id, t1.object_version_number, t1.created_by, t1.creation_date, t1.last_updated_by, t1.last_update_date, t1.type_code, t1.project_id, t1.referenced, t1.source, t1.reference_id, t1.is_initialize AS initialize, 1 as enabled, t1.NAME AS NAME, t1.description AS description, t1.icon AS icon, t1.colour AS colour FROM fd_issue_type t1 JOIN fd_issue_type_extend t2 ON t1.id = t2.issue_type_id WHERE t1.organization_id = #{organizationId} AND t1.project_id = 0 AND t1.source = 'system' )

        SELECT temp.* FROM (
            SELECT
                it.id,
                it.type_code,
                IFNULL( ite.name, it.name ) AS name,
                ite.project_id,
                ite.enabled,
		        ite.rank
            FROM fd_issue_type_extend ite
            JOIN fd_issue_type it ON it.id = ite.issue_type_id
            WHERE ite.organization_id = #{organizationId, jdbcType=DECIMAL}
            AND ite.project_id IN

                #{projectId, jdbcType=DECIMAL}


            UNION ALL

            SELECT
                it.id,
                it.type_code,
                it.NAME,
                it.project_id,
                1 AS enabled,
                null AS `rank`
            FROM fd_issue_type it
            WHERE it.organization_id = #{organizationId, jdbcType=DECIMAL}
            AND it.source = '${@io.choerodon.agile.infra.enums.IssueTypeSource@SYSTEM}'
            AND it.project_id = ${@io.choerodon.agile.app.service.impl.IssueTypeServiceImpl@ZERO}

            AND (SELECT COUNT(*) FROM fd_issue_type_extend ite WHERE ite.issue_type_id = it.id AND ite.project_id IN

                        #{projectId, jdbcType=DECIMAL}



                        AND ite.enabled = ${@org.hzero.core.base.BaseConstants$Flag@YES}

                ) < ${projectIds.size()}
        ) temp



                    AND temp.id NOT IN

                        #{filterIssueTypeId}



                    AND temp.type_code NOT IN

                        #{excludeTypeCode}




                    AND temp.name LIKE #{paramLike}



                    AND temp.enabled = ${@org.hzero.core.base.BaseConstants$Flag@YES}



                AND EXISTS (
					SELECT 1 FROM fd_issue_type_scheme_config tsc WHERE tsc.issue_type_id = temp.id AND tsc.scheme_id IN

                        #{issueTypeSchemeIds}

				)


        ORDER BY temp.rank DESC, temp.id DESC
