mapper.delete:
delete from issue_user_rel
where
project_id = #{projectId}
and issue_id = #{issueId}

    and user_type = #{userType}


    and user_id in

        #{userId}

mapper.select:
select user_id
from issue_user_rel
where issue_id = #{issueId}
and project_id = #{projectId}

    and user_type = #{userType}
