mapper.update:

update fd_object_scheme_field_extend
set is_required = #{required},
last_updated_by = #{audit.user},
last_update_date = #{audit.now}
where organization_id = #{organizationId}

    and issue_type = #{issueType}

and field_id = #{fieldId}

mapper.select:
select min(rank)
from fd_object_scheme_field_extend
where organization_id = #{organizationId}
and issue_type = #{issueType}


        AND project_id = #{projectId}


        AND project_id is null

select * from fd_object_scheme_field_extend
where 1=1

    and issue_type = #{issueType}

and organization_id = #{organizationId}
and field_id = #{fieldId}
and project_id is null

select t4.id, t4.field_id, t6.name as field_name, t6.default_value, t4.is_required as required, t4.is_created as created, t4.is_edited as edited, t4.issue_type, t4.rank, t4.object_version_number, case when t6.project_id is null then 'organization' else 'project' end as created_level from fd_object_scheme_field_extend t4 join ( select t1.id from fd_object_scheme_field_extend t1 where t1.organization_id = #{organizationId} and t1.issue_type = #{issueType} and t1.project_id is null
and t1.field_id not in ( select t3.field_id from fd_object_scheme_field_extend t3 where t3.organization_id = #{organizationId} and t3.issue_type = #{issueType} and t3.project_id = #{projectId} ) union select t2.id from fd_object_scheme_field_extend t2 where t2.organization_id = #{organizationId} and t2.issue_type = #{issueType} and t2.project_id = #{projectId}
) t5 on t4.id = t5.id left join fd_object_scheme_field t6 on t4.field_id = t6.id
