mapper.delete:
delete t1.*, t2.*
from fd_object_scheme_field t1
left join fd_object_scheme_field_extend t2 on (
    t1.id = t2.field_id
    and t2.organization_id = #{organizationId}

        and t2.project_id = #{projectId}

)
where t1.id = #{fieldId}

mapper.select:
SELECT  osf.*,
flv.name as field_type_name
FROM fd_object_scheme_field osf
LEFT JOIN lookup_value flv ON flv.value_code = osf.field_type and flv.type_code = 'field_type'
WHERE
(osf.is_system = 1
OR
(
    osf.organization_id = #{organizationId}
    AND
    (
        osf.project_id is null

            or osf.project_id in

                #{projectId}


    )

    AND osf.id IN (
        SELECT
        osfe.field_id
        FROM
        fd_object_scheme_field_extend osfe
        WHERE
        osfe.field_id = osf.id


                and osfe.issue_type in

                    #{item}




                    AND osfe.issue_type IN ('issue_epic', 'story', 'bug', 'task', 'sub_task')


                    AND osfe.issue_type IN ('issue_epic', 'feature')



    )

))

    AND osf.context = #{searchVO.context}


    AND osf.scheme_code = #{searchVO.schemeCode}


    AND osf.name = #{searchVO.name}


    AND osf.code = #{searchVO.code}

order by osf.id asc

SELECT osf.*, flv.NAME AS field_type_name FROM fd_object_scheme_field osf LEFT JOIN lookup_value flv ON flv.value_code = osf.field_type AND flv.type_code = 'field_type' WHERE ( osf.is_system = 0 AND ( osf.organization_id = #{organizationId} AND osf.project_id IS NULL
or osf.project_id in
#{projectId}
)) AND osf.code IN
#{fieldCode}

select * from fd_object_scheme_field t2 join ( select distinct field_id from fd_object_scheme_field_extend t1 where t1.id in
#{id}
) t3 on t2.id = t3.field_id

select t4.id, t4.code, t4.default_value, t4.description, t4.is_required, t4.field_type, flv.name as field_type_name, t4.name, t4.object_version_number, t4.organization_id, t4.project_id, t4.is_system, t4.extra_config, case when t4.organization_id = 0 then 'system' when t4.organization_id != 0 and t4.project_id is null then 'organization' else 'project' end as created_level, t5.id as extend_id, fit.type_code as extend_issue_type, fit.name as extend_issue_type_name, t5.is_required as extend_field_required, t5.issue_type_id as extend_issue_type_id from fd_object_scheme_field t4 join (
) t5 on t4.id = t5.field_id left join fd_issue_type fit on (fit.organization_id = #{organizationId} and t5.issue_type_id = fit.id) left join lookup_value flv on flv.value_code = t4.field_type and flv.type_code = 'field_type' where 1=1
and t4.id = #{fieldId}
and t4.scheme_code = #{schemeCode}
and t4.scheme_code = 'agile_issue'
order by t4.is_system desc, t4.id asc

mapper.sql:
select
    t1.id,
    t1.field_id,
    t1.issue_type_id,
    t1.is_required,
    t1.default_value
from fd_object_scheme_field_extend t1
where t1.organization_id = #{organizationId}
and t1.project_id is null

    and t1.issue_type_id = #{issueTypeId}


    and t1.issue_type in

        #{issueType}



    and t1.field_id not in (
    select t3.field_id
    from fd_object_scheme_field_extend t3
    where t3.organization_id = #{organizationId}
    and t3.project_id = #{projectId}

        and t3.issue_type_id = #{issueTypeId}


        and t3.issue_type in

            #{issueType}


    )
    union
    select
    t2.id,
    t2.field_id,
    t2.issue_type_id,
    t2.is_required,
    t2.default_value
    from fd_object_scheme_field_extend t2
    where t2.organization_id = #{organizationId}
    and t2.project_id = #{projectId}

        and t2.issue_type_id = #{issueTypeId}


        and t2.issue_type in

            #{issueType}

mapper.select:
SELECT t4.id, t4.code, t4.field_type, flv.name as field_type_name, t4.name, t4.object_version_number, t4.organization_id, t4.project_id, t4.is_system FROM fd_object_scheme_field t4 JOIN () t5 ON t4.id = t5.field_id LEFT JOIN fd_issue_type fit on (fit.organization_id = #{organizationId} and t5.issue_type_id = fit.id) LEFT JOIN lookup_value flv on flv.value_code = t4.field_type and flv.type_code = 'field_type' WHERE t4.field_type in ('member','multiMember') AND t4.is_system = 0
AND t4.scheme_code = #{schemeCode}
AND t4.scheme_code = 'agile_issue'
AND t5.issue_type_id = #{issueTypeId}
AND t4.code IN
#{fieldCode}

select fosf.id, fosf.code, fosf.default_value, fosf.description, fosf.field_type, flv.name as field_type_name, fosf.name, fosf.object_version_number, fosf.organization_id, fosf.project_id, fosf.is_system, fosf.is_required, fosf.extra_config, 'system' as created_level from fd_object_scheme_field fosf left join lookup_value flv on flv.value_code = fosf.field_type and flv.type_code = 'field_type' where fosf.organization_id = 0 AND fosf.project_id = 0 AND fosf.is_system = 1 AND fosf.id NOT IN
#{systemFieldId}
AND fosf.context != 'backlog'

select fosf.id, fosf.code, fosf.default_value, fosf.description, fosf.field_type, flv.name as field_type_name, fosf.name, fosf.object_version_number, fosf.organization_id, fosf.project_id, fosf.is_system, fosf.is_required, fosf.extra_config, 'system' as created_level from fd_object_scheme_field fosf left join lookup_value flv on flv.value_code = fosf.field_type and flv.type_code = 'field_type' where fosf.organization_id = 0 and fosf.project_id = 0 and fosf.is_system = 1 and fosf.id not in ( select t1.field_id from fd_object_scheme_field_extend t1 where t1.organization_id = #{organizationId} and t1.project_id is null
and t1.issue_type_id = #{issueTypeId}
and t1.field_id not in ( select t3.field_id from fd_object_scheme_field_extend t3 where t3.organization_id = #{organizationId} and t3.project_id = #{projectId}
and t3.issue_type_id = #{issueTypeId}
) union select t2.field_id from fd_object_scheme_field_extend t2 where t2.organization_id = #{organizationId} and t2.project_id = #{projectId}
and t2.issue_type_id = #{issueTypeId}
)

SELECT osf.id, osf.code, osf.field_type, osf.extra_config, osfe.id as extend_id, osfe.issue_type_id as extend_issue_type_id FROM fd_object_scheme_field osf LEFT JOIN fd_object_scheme_field_extend osfe ON osf.id = osfe.field_id AND osfe.organization_id = #{organizationId} WHERE osf.organization_id = #{organizationId} AND ( osfe.project_id IS NULL OR osfe.project_id = #{projectId} ) AND osf.id IN
#{fieldId}
AND osfe.issue_type_id IN
#{issueTypeId}

SELECT  osf.*,
flv.name as field_type_name
FROM fd_object_scheme_field osf
LEFT JOIN lookup_value flv ON flv.value_code = osf.field_type and flv.type_code = 'field_type'
WHERE
(osf.is_system = 1
OR
(
osf.organization_id = #{organizationId}
AND
(
osf.project_id is null

    or osf.project_id in

        #{projectId}


)

    AND osf.id IN (
    SELECT
    osfe.field_id
    FROM
    fd_object_scheme_field_extend osfe
    WHERE
    osfe.field_id = osf.id


            and osfe.issue_type in

                #{item}




                AND osfe.issue_type IN ('issue_epic', 'story', 'bug', 'task', 'sub_task')


                AND osfe.issue_type IN ('issue_epic', 'feature')



    )

))

    AND osf.context = #{searchVO.context}


    AND osf.scheme_code = #{searchVO.schemeCode}


    AND osf.name = #{searchVO.name}


    AND osf.code = #{searchVO.code}

order by osf.id asc
