mapper.update:
UPDATE agile_quick_filter
SET sequence = (sequence + #{add})
WHERE sequence >= #{sequence} AND project_id = #{projectId} and filter_id !=#{filterId}

mapper.select:
SELECT aqf.sequence
FROM
    agile_quick_filter aqf
WHERE aqf.project_id = #{projectId}
ORDER BY aqf.sequence DESC
LIMIT 1;

SELECT aqf.sequence
FROM
    agile_quick_filter aqf
WHERE aqf.project_id = #{projectId} AND aqf.sequence < #{sequence}
ORDER BY aqf.sequence DESC
LIMIT 1;

SELECT aqf.sequence
FROM
    agile_quick_filter aqf
WHERE aqf.project_id = #{projectId} AND aqf.sequence > #{sequence}
ORDER BY aqf.sequence ASC
LIMIT 1;

SELECT * FROM agile_quick_filter WHERE project_id = #{projectId}
and name LIKE CONCAT(CONCAT('%', #{filterName}), '%')
and filter_id in
#{filterId}
and filter_id not in
#{ignoredQuickFilterId}
AND ( name LIKE CONCAT(CONCAT('%', #{content}), '%') OR express_query LIKE CONCAT(CONCAT('%', #{content}), '%') OR description LIKE CONCAT(CONCAT('%', #{content}), '%') )
