SELECT ai.assignee_id FROM agile_issue ai where ai.assignee_id is not null and ai.assignee_id != 0 AND ai.project_id = #{projectId} AND ai.issue_id IN
#{issueId}
UNION ALL SELECT ai.assignee_id FROM agile_issue ai WHERE ai.assignee_id is not null and ai.assignee_id != 0 AND ai.project_id = #{projectId} AND ai.type_code = 'sub_task' AND ai.parent_issue_id IN
#{issueId}

SELECT ai.issue_id, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num, ai.type_code, ai.summary, ai.reporter_id, ai.description, ai.assignee_id, ai.project_id, ai.epic_id, asp1.sprint_id as is_sprint_id, ai.story_points, ai.status_id, ai.priority_id, ai.issue_type_id, ai.object_version_number as is_object_version_number, ai.priority_code, ai1.epic_name AS issue_epic_name, ai.estimated_start_time, ai.estimated_end_time, alv2.name AS color, IF(ai.parent_issue_id IS NULL OR ai.parent_issue_id = 0, (IF(ai.relate_issue_id IS NULL OR ai.relate_issue_id = 0, null, ai.relate_issue_id)), ai.parent_issue_id) as parent_id, -- feature_info.feature_name AS feature_name, apv1.version_id AS version_id, apv1.name AS version_name FROM (select ai3.* from agile_issue ai3 where ai3.project_id = #{projectId} AND ai3.issue_id in
#{issueId}
)ai LEFT JOIN agile_issue_status ais ON ai.status_id = ais.status_id and ai.project_id = ais.project_id LEFT JOIN agile_project_info api ON ai.project_id = api.project_id LEFT JOIN agile_issue ai1 ON ai.epic_id = ai1.issue_id -- LEFT JOIN ( -- select -- aio.issue_id as issue_id, -- aio.summary as feature_name -- from agile_issue aio, agile_feature af -- where aio.type_code = 'feature' -- and aio.issue_id = af.issue_id -- ) feature_info ON ai.feature_id = feature_info.issue_id LEFT JOIN lookup_value alv2 ON ai1.color_code = alv2.value_code LEFT JOIN (SELECT aisr.issue_id, asp.sprint_id FROM agile_issue_sprint_rel aisr, agile_sprint asp WHERE aisr.sprint_id = asp.sprint_id AND asp.status_code != 'closed' ) asp1 ON asp1.issue_id = ai.issue_id LEFT JOIN (SELECT avir.issue_id, avir.version_id, apv.name FROM agile_version_issue_rel avir, agile_product_version apv WHERE avir.version_id = apv.version_id AND avir.relation_type = 'fix' ) apv1 ON apv1.issue_id = ai.issue_id WHERE asp1.sprint_id is null and ais.is_completed != 1 and ai.type_code in ('story', 'task', 'bug','sub_task') and ai.apply_type = 'agile' and ai.project_id = #{projectId} ORDER BY ai.rank asc

