mapper.sql:
SELECT ai.issue_id, ai.assignee_id, aisr.sprint_id FROM agile_issue ai,agile_issue_sprint_rel aisr WHERE ai.issue_id = aisr.issue_id AND ai.project_id = #{projectId} AND ai.type_code in ('story', 'task', 'bug','sub_task') AND aisr.sprint_id in
#{sprintId}
AND ai.issue_id IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id = #{advancedSearchArgs.versionId} AND relation_type = 'fix')
AND ai.epic_id = #{advancedSearchArgs.epicId}
AND ai.issue_id NOT IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id != 0 AND relation_type = 'fix')
AND (ai.epic_id IS NULL OR ai.epic_id = 0)
and ai.type_code = 'story' AND ai.feature_id = #{advancedSearchArgs.featureId}
and ai.type_code = 'story' AND (ai.feature_id IS NULL OR ai.feature_id = 0)
AND ai.assignee_id = #{userId}
AND ai.type_code = 'story'
AND ai.assignee_id IN
#{assigneeFilterId}

mapper.select:
SELECT cc.sprint_id, cc.assignee_id, cc.total_story_points, cc.total_remaining_time, cc.issue_count, bb.remaining_story_points, bb.remaining_time, bb.remaining_issue_count FROM ( SELECT a.sprint_id, a.assignee_id, SUM( a.story_points ) AS total_story_points, SUM( a.remaining_time ) AS total_remaining_time, COUNT( 1 ) AS issue_count FROM ( SELECT aisr.sprint_id, CASE WHEN ai.assignee_id IS NULL THEN 0 ELSE ai.assignee_id END AS assignee_id, ai.story_points, (ai.remaining_time + IFNULL(wl.work_time,0)) as remaining_time FROM agile_issue ai JOIN agile_issue_sprint_rel aisr ON aisr.issue_id = ai.issue_id LEFT JOIN ( select wl1.issue_id, SUM(wl1.work_time) as work_time from agile_work_log wl1 where wl1.issue_id in
#{issueId}
group by wl1.issue_id ) wl on ai.issue_id = wl.issue_id WHERE aisr.project_id = #{projectId} AND aisr.sprint_id = #{sprintId} AND ai.issue_id in
#{issueId}
) a GROUP BY a.assignee_id ) cc LEFT JOIN ( SELECT a2.sprint_id, a2.assignee_id, SUM( a2.story_points ) AS remaining_story_points, SUM( a2.remaining_time ) AS remaining_time, COUNT( 1 ) AS remaining_issue_count FROM ( SELECT aisr.sprint_id, CASE WHEN ai2.assignee_id IS NULL THEN 0 ELSE ai2.assignee_id END AS assignee_id, ai2.story_points, ai2.remaining_time FROM agile_issue ai2, agile_issue_sprint_rel aisr WHERE aisr.issue_id = ai2.issue_id AND aisr.project_id = #{projectId} AND aisr.sprint_id = #{sprintId} AND ai2.issue_id in
#{issueId}
AND ai2.status_id IN ( SELECT ais.status_id FROM agile_issue_status ais WHERE ais.project_id = #{projectId} AND ais.is_completed != 1 ) ) a2 GROUP BY a2.assignee_id ) bb ON cc.assignee_id = bb.assignee_id order by cc.assignee_id

SELECT asp.sprint_name, asp.sprint_id, asp.start_date, asp.end_date, asp.status_code, asp.actual_end_date FROM agile_sprint asp WHERE asp.project_id = #{projectId} and asp.status_code != 'sprint_planning' and (asp.start_date >= #{startDate} or (asp.start_date <= #{startDate} and asp.end_date >= #{startDate}))
and asp.start_date <= #{endDate}
order by asp.start_date asc

SELECT sprint_id, sprint_name, start_date, end_date, actual_end_date, status_code, project_id FROM agile_sprint WHERE project_id in
#{projectId}
AND status_code IN
#{sprintStatusCode}
ORDER BY sprint_id DESC

SELECT *
FROM agile_sprint
WHERE sprint_id = #{sprintId} AND project_id = #{projectId}

SELECT MIN(ai.rank) FROM agile_issue ai LEFT JOIN (SELECT aisr1.sprint_id, aisr1.issue_id FROM agile_issue_sprint_rel aisr1, agile_sprint asp WHERE aisr1.project_id = #{projectId} AND aisr1.sprint_id = asp.sprint_id AND asp.status_code != 'closed') aisr ON aisr.issue_id = ai.issue_id WHERE ai.project_id = #{projectId} AND ai.type_code in ('story', 'task', 'bug')
AND aisr.sprint_id = #{sprintId}
AND (aisr.sprint_id IS NULL OR aisr.sprint_id = 0)

SELECT
  ai.issue_id as issue_id
FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr
WHERE ai.project_id = #{projectId}
      AND aisr.issue_id = ai.issue_id
      AND aisr.sprint_id = #{sprintId}
      AND ai.status_id = ais.status_id
      AND ais.project_id = #{projectId}
      AND ais.is_completed = 1
      AND ai.issue_id IN (SELECT ai2.parent_issue_id
                          FROM agile_issue ai2, agile_issue_status ais2, agile_issue_sprint_rel aisr2
                          WHERE ai2.project_id = #{projectId}
                                AND aisr2.issue_id = ai2.issue_id
                                AND aisr2.sprint_id = #{sprintId}
                                AND ai2.type_code = 'sub_task'
                                AND ai2.status_id = ais2.status_id
                                AND ais2.project_id = #{projectId}
                                AND ais2.is_completed != 1)

SELECT
  ai.issue_id as issue_id
FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr
WHERE ai.project_id = #{projectId}
      AND aisr.issue_id = ai.issue_id
      AND aisr.sprint_id = #{sprintId}
      AND ai.status_id = ais.status_id
      AND ais.project_id = #{projectId}
      AND ais.is_completed = 1
      AND ai.issue_id IN (SELECT ai2.parent_issue_id
                          FROM agile_issue ai2, agile_issue_status ais2, agile_issue_sprint_rel aisr2
                          WHERE ai2.project_id = #{projectId}
                                AND aisr2.issue_id = ai2.issue_id
                                AND aisr2.sprint_id = #{sprintId}
                                AND ai2.type_code = 'sub_task'
                                AND ai2.status_id = ais2.status_id
                                AND ais2.project_id = #{projectId}
                                AND ais2.is_completed != 1)
UNION ALL
select ai3.issue_id as issue_id
from agile_issue ai3
where ai3.parent_issue_id in
(SELECT
  ai.issue_id as issue_id
FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr
WHERE ai.project_id = #{projectId}
      AND aisr.issue_id = ai.issue_id
      AND aisr.sprint_id = #{sprintId}
      AND ai.status_id = ais.status_id
      AND ais.project_id = #{projectId}
      AND ais.is_completed = 1
      AND ai.issue_id IN (SELECT ai2.parent_issue_id
                          FROM agile_issue ai2, agile_issue_status ais2, agile_issue_sprint_rel aisr2
                          WHERE ai2.project_id = #{projectId}
                                AND aisr2.issue_id = ai2.issue_id
                                AND aisr2.sprint_id = #{sprintId}
                                AND ai2.type_code = 'sub_task'
                                AND ai2.status_id = ais2.status_id
                                AND ais2.project_id = #{projectId}
                                AND ais2.is_completed != 1))

SELECT cc.sprint_id, cc.assignee_id, cc.total_story_points, cc.total_remaining_time, cc.issue_count, bb.remaining_story_points, bb.remaining_time, bb.remaining_issue_count FROM ( SELECT a.sprint_id, a.assignee_id, SUM( a.story_points ) AS total_story_points, SUM( a.remaining_time ) AS total_remaining_time, COUNT( 1 ) AS issue_count FROM ( SELECT aisr.sprint_id, CASE WHEN ai.assignee_id IS NULL THEN 0 ELSE ai.assignee_id END AS assignee_id, ai.story_points, ai.remaining_time FROM agile_issue ai, agile_issue_sprint_rel aisr WHERE aisr.issue_id = ai.issue_id AND aisr.project_id = #{projectId} AND aisr.sprint_id in
#{sprintId}
) a GROUP BY a.sprint_id,a.assignee_id ) cc LEFT JOIN ( SELECT a2.sprint_id, a2.assignee_id, SUM( a2.story_points ) AS remaining_story_points, SUM( a2.remaining_time ) AS remaining_time, COUNT( 1 ) AS remaining_issue_count FROM ( SELECT aisr.sprint_id, CASE WHEN ai2.assignee_id IS NULL THEN 0 ELSE ai2.assignee_id END AS assignee_id, ai2.story_points, ai2.remaining_time FROM agile_issue ai2, agile_issue_sprint_rel aisr WHERE aisr.issue_id = ai2.issue_id AND aisr.project_id = #{projectId} AND aisr.sprint_id in
#{sprintId}
AND ai2.status_id IN ( select ais1.status_id from ( SELECT ais.status_id FROM agile_issue_status ais WHERE ais.project_id = #{projectId} AND ais.is_completed != 1 ) ais1 ) ) a2 GROUP BY a2.sprint_id,a2.assignee_id ) bb ON cc.sprint_id = bb.sprint_id and cc.assignee_id = bb.assignee_id order by cc.sprint_id,cc.assignee_id

select ai1.sprint_id as sprint_id, SUM(case when ais.category_code = 'todo' then ai1.story_points else 0 end) as todoStoryPoint, SUM(case when ais.category_code = 'doing' then ai1.story_points else 0 end) as doingStoryPoint, SUM(case when ais.category_code = 'done' then ai1.story_points else 0 end) as doneStoryPoint from ( select ai.issue_id,ai.status_id,aisr.sprint_id,ai.story_points from agile_issue ai JOIN agile_issue_sprint_rel aisr on aisr.project_id = ai.project_id and aisr.issue_id = ai.issue_id where ai.project_id = #{projectId} and ai.type_code = 'story' and ai.story_points is not null and aisr.sprint_id in
#{sprintId}
) ai1 LEFT JOIN agile_issue_status ais on ais.project_id = #{projectId} and ais.status_id = ai1.status_id group by ai1.sprint_id

select ai.issue_id from agile_issue ai
where ai.project_id = #{projectId}

    AND EXISTS (
        select
            1
        from agile_issue_sprint_rel aisr
        where aisr.project_id = #{projectId}
        and aisr.sprint_id = #{sprintId}
        and aisr.issue_id = ai.issue_id
        )


    AND NOT EXISTS (
        select
        1
        from agile_issue_sprint_rel aisr
        where aisr.project_id = #{projectId}
        and aisr.issue_id = ai.issue_id
    )

order by ai.rank desc limit 1

SELECT * FROM agile_sprint WHERE project_id IN
#{projectId}
AND status_code = 'started'

select ai1.issue_id from ( SELECT distinct case when ai.type_code = 'sub_task' then ai.parent_issue_id when ai.type_code = 'bug' and ai.relate_issue_id is not null and ai.relate_issue_id != 0 then ai.relate_issue_id else ai.issue_id end as issue_id FROM agile_issue ai LEFT JOIN agile_issue_sprint_rel aisr ON aisr.issue_id = ai.issue_id LEFT JOIN agile_sprint asp on asp.sprint_id = aisr.sprint_id WHERE ai.project_id = #{projectId} AND asp.sprint_id = #{sprintId}
AND ai.issue_id IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id = #{advancedSearchArgs.versionId} AND relation_type = 'fix')
AND ai.epic_id = #{advancedSearchArgs.epicId}
AND ai.issue_id NOT IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id != 0 AND relation_type = 'fix')
AND (ai.epic_id IS NULL OR ai.epic_id = 0)
and ai.type_code = 'story' AND ai.feature_id = #{advancedSearchArgs.featureId}
and ai.type_code = 'story' AND (ai.feature_id IS NULL OR ai.feature_id = 0)
AND ai.type_code = 'story'
AND ai.assignee_id IN
#{assigneeFilterId}
) aii JOIN agile_issue ai1 on aii.issue_id = ai1.issue_id where ai1.project_id = #{projectId} AND ai1.type_code in ('story', 'task', 'bug') order by ai1.rank ASC

