mapper.select:
select fsfs.id, fsfs.project_id, fsfs.issue_type_id, fsfs.status_id, fsfs.field_id, fosf.name as fieldName, fosf.field_type, fosf.code as fieldCode, fosf.is_system from fd_status_field_setting fsfs,fd_object_scheme_field fosf where fsfs.project_id = #{projectId} AND fsfs.issue_type_id = #{issueTypeId} AND fsfs.status_id in
#{statusId}
AND fsfs.field_id = fosf.id

select fsfs.id, fsfs.project_id, fsfs.issue_type_id, fsfs.status_id, fsfs.field_id, fosf.name as fieldName, fosf.field_type, fosf.code as fieldCode, fosf.is_system from fd_status_field_setting fsfs,fd_object_scheme_field fosf where fsfs.organization_id = #{organizationId} AND fsfs.project_id = 0 AND fsfs.issue_type_id = #{issueTypeId} AND fsfs.status_id in
#{statusId}
AND fsfs.field_id = fosf.id

mapper.delete:
delete from fd_status_field_setting


        and organization_id = #{organizationId}


        and project_id = #{projectId}

    and issue_type_id = #{issueTypeId}
