mapper.select:
select * from fd_status_linkage fsl
where fsl.project_id = #{projectId}
AND fsl.issue_type_id = #{issueTypeId}
AND fsl.status_id in

    #{statusId}

select * from fd_status_linkage fsl
where fsl.project_id = #{projectId}
AND fsl.parent_issue_type_id = #{parentIssueTypeId}
AND fsl.parent_issue_status_setting = #{parentChangeStatusId}
AND fsl.issue_type_id in

    #{issueTypeId}

SELECT fsl.* FROM fd_status_linkage fsl
JOIN fd_issue_type fit on fit.id = fsl.issue_type_id
WHERE fsl.project_id = #{projectId}
AND fsl.issue_type_id  = #{issueTypeId} AND fsl.status_id = #{statusId}
UNION ALL
SELECT fsl.* FROM fd_status_linkage fsl
JOIN fd_issue_type fit on fit.id = fsl.parent_issue_type_id
WHERE fsl.project_id = #{projectId}
AND fsl.parent_issue_type_id = #{issueTypeId}
AND fsl.parent_issue_status_setting = #{statusId}

select * from fd_status_linkage fsl
where  fsl.organization_id = #{organizationId}
AND fsl.project_id = 0
AND fsl.issue_type_id = #{issueTypeId}
AND fsl.status_id in

    #{statusId}

mapper.delete:
delete from fd_status_linkage


        and organization_id = #{organizationId}


        and project_id = #{projectId}

    and (parent_issue_type_id = #{issueTypeId} or issue_type_id = #{issueTypeId})
