mapper.select:
select * from fd_status_machine_scheme_config
where organization_id = #{organizationId}
and scheme_id in

    #{item}

select
    smsc.state_machine_id
from fd_status_machine_scheme_config smsc
where
 smsc.organization_id = #{organizationId}
 AND smsc.scheme_id in (
   select scheme_id from fd_project_config
   where project_id = #{projectId}
   and scheme_type = 'scheme_state_machine'
   and apply_type = #{applyType}
 )
 AND smsc.issue_type_id = #{issueTypeId}

select
fsmc.*,
fpc.apply_type
from fd_status_machine_scheme_config fsmc
left join fd_project_config fpc on fpc.scheme_id  = fsmc.scheme_id
where fpc.project_id = #{projectId}
and fsmc.organization_id = #{organizationId}
and fpc.scheme_type = 'scheme_state_machine'
and fpc.apply_type in

    #{applyType}
