mapper.select:
select fsts.* from fd_status_transfer_setting fsts
where fsts.project_id = #{projectId}
AND fsts.issue_type_id = #{issueTypeId}
AND fsts.status_id in

    #{statusId}

select fsts.* from fd_status_transfer_setting fsts
where fsts.organization_id = #{organizationId}
AND fsts.project_id = 0
AND fsts.issue_type_id = #{issueTypeId}
AND fsts.status_id in

    #{statusId}
