mapper.select:
select ai.issue_id
from agile_issue ai
where ai.project_id = #{projectId}
and ai.type_code = 'issue_epic'

    AND ai.sprint_id = #{sprintId}

order by ai.issue_id desc

select ai.issue_id as ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) as ai_issue_num, ai.type_code as ai_type_code, ai.summary as ai_summary, ai.epic_name as ai_epic_name, -- ai.program_id as ai_program_id, ar.rank as ar_rank, ar.object_version_number as ar_object_version_number from agile_issue ai LEFT JOIN agile_project_info api ON ai.project_id = api.project_id left join agile_rank ar on ar.project_id = #{projectId} and ai.issue_id = ar.issue_id and ar.type = 'epic' where ai.issue_id in
#{epicId}
AND ai.sprint_id = #{sprintId}
order by ai.issue_id desc

select ai.issue_id as ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) as ai_issue_num, ai.summary as ai_summary, ai.epic_id as ai_epic_id, -- ai.feature_id as ai_feature_id, version_info.version_id as version_version_id, version_info.name as version_name from agile_issue ai LEFT JOIN agile_project_info api ON ai.project_id = api.project_id left join ( select avir.issue_id, apv.version_id, apv.name from agile_version_issue_rel avir, agile_product_version apv where avir.version_id = apv.version_id and avir.project_id = #{projectId} ) version_info on ai.issue_id = version_info.issue_id where ai.project_id = #{projectId} and ai.type_code = 'story' and (
ai.epic_id in
#{epicId}
)
and ai.issue_id in ( select avir2.issue_id from agile_version_issue_rel avir2 where avir2.version_id in
#{versionId}
)
and ai.status_id in
#{statusId}
AND ai.sprint_id = #{sprintId}
order by ai.issue_id

select ai.issue_id as ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) as ai_issue_num, ai.summary as ai_summary, ai.issue_type_id as ai_issue_type_id, ai.status_id as ai_status_id, ais.is_completed AS ai_completed, version_info.version_id as version_version_id, version_info.name as version_name from agile_issue ai LEFT JOIN agile_project_info api ON ai.project_id = api.project_id LEFT JOIN agile_issue_status ais ON ai.status_id = ais.status_id and ais.project_id = #{projectId} left join ( select avir.issue_id, apv.version_id, apv.name from agile_version_issue_rel avir, agile_product_version apv where avir.version_id = apv.version_id and avir.project_id = #{projectId} ) version_info on ai.issue_id = version_info.issue_id where ai.project_id = #{projectId} and ai.type_code = 'story' and (ai.epic_id is null or ai.epic_id = 0)
AND ai.assignee_id = #{searchVO.searchArgs.assigneeId}
and (ai.issue_id in ( select avir2.issue_id from agile_version_issue_rel avir2 where avir2.version_id in
#{versionId}
) or version_info.version_id is null )
and ai.status_id in
#{statusId}
order by ai.issue_id desc
