mapper.select:
SELECT *
FROM agile_work_log
WHERE project_id IN

    #{projectId}

AND issue_id IN

    #{issueId}

SELECT issue_id, sum(ifnull(work_time, 0)) as work_time FROM agile_work_log WHERE project_id IN
#{projectId}
AND issue_id IN
#{issueId}
group by issue_id

mapper.update:
update agile_work_log set project_id = #{targetProjectId}
where project_id = #{projectId}
AND issue_id = #{issueId}
